import React from 'react'
import Proptypes from 'prop-types'
import { useState } from 'react'

const Tasks = () => {

	const [tasks, setTasks] = useState([
		{
			id:1,
			text: "appointment",
			day: "sept 28",
			reminder: true,
		},

		{
			id:2,
			text: "appointment 2",
			day: "sept 29",
			reminder: false,
		},

	])

	return (
		<>
			{tasks.map((task)=>
				(<h3 key={task.id}>{task.text}</h3>
			))}
		</>
	)
}

export default Tasks
