import React from 'react'
import Proptypes from 'prop-types'

const tasks = [
	{
		id:1,
		text: "appointment",
		day: "sept 28",
		reminder: true,
	},

	{
		id:2,
		text: "appointment 2",
		day: "sept 29",
		reminder: true,
	},

]

const Tasks = () => {

	return (
		<>
		{tasks.map((task)=>(<h3>{task.text}</h3>))}
		</>
	)
}

export default Tasks
