import React, {useRef, useEffect, useState} from 'react'
import './App.css';
import BarChart from './BarChart'
import {select, axisBottom, axisRight, scaleLinear, scaleBand} from "d3"

function App() {

  const getData= ()=>{
     fetch('http://127.0.0.1:5000/APPL', {
     // fetch('https://api.randomuser.me', {

    headers: {
      "Access-Control-Allow-Origin": "*"
    },
     })
    .then(response => response.json())
    .then(data => console.log(data))
    .catch((error) => {
      console.error(error)
    });
  };

  // const getData = async() => {
  //   const api_call = await fetch(`http://127.0.0.1:5000/APPL`, {
  //     mode: "no-cors"
  //   });
  //   // const data = await api_call.json();
  //   const data = await api_call.json();
  //   console.log(data);
  //   }


   useEffect(()=>{
     getData()
   },[])

  const [data, setData] = useState([10,20,40,50,100,20,100,300]);

  return (
    <React.Fragment>
    <BarChart data={data}/>
      <br/>
      <br/>
      <br/>
      <br/>
      <button onClick={()=>setData(data.map(value=>value+5))}>
        increase
      </button>
      <button onClick={()=>setData(data.filter(value=>value<30))}>
        filter
      </button>
      <button onClick={()=>setData([...data,Math.round(Math.random()*100)])}>
        Add data
      </button>
    </React.Fragment>
  );
}

export default App;
