  const getData = async() => {
    const api_call = await fetch(`http://127.0.0.1:5000/APPL`, {
      method: 'GET', // *GET, POST, PUT, DELETE, etc.
          mode: 'cors', // no-cors, *cors, same-origin
          cache: 'no-cache', // *default, no-cache, reload, force-cache, only-if-cached
          credentials: 'same-origin', // include, *same-origin, omit
          headers: {
            'Content-Type': 'application/json'
            // 'Content-Type': 'application/x-www-form-urlencoded',
          },
          redirect: 'follow', // manual, *follow, error
          referrerPolicy: 'no-referrer', // no-referrer, *no-referrer-when-downgrade, origin, origin-when-cross-origin, same-origin, strict-origin, strict-origin-when-cross-origin, unsafe-url
          body: JSON.stringify(data) // body data type must match "Content-Type" header
    });
    // const data = await api_call.json();
    const data = await api_call.json();
    console.log(data);
    }


   useEffect(()=>{
     getData()
   },[])

  const [data, setData] = useState([10,20,40,50,100,20,100,300]);

  return (
    <React.Fragment>
    <BarChart data={data}/>
      <br/>
      <br/>
      <br/>
      <br/>
      <button onClick={()=>setData(data.map(value=>value+5))}>
        increase
      </button>
      <button onClick={()=>setData(data.filter(value=>value<30))}>
        filter
      </button>
      <button onClick={()=>setData([...data,Math.round(Math.random()*100)])}>
        Add data
      </button>
    </React.Fragment>
  );
}

export default App;
